/// !!!!!!
/// very nice solution. Use stack. Think carefully about when to push, when to pop
/// things in stack is in non-decreasing order
class Solution {
public:
    int largestRectangleArea(vector<int> &height) {
		stack<int> accendingPos;
		height.push_back(0);
		int area = 0;
		for(int i=0;i<height.size();)
		{
			if(accendingPos.empty() || height[accendingPos.top()] <= height[i] )
			{
				accendingPos.push(i);
				++i;
			}
			else
			{
				int h = height[accendingPos.top()];
				accendingPos.pop();
				area = max(area, h * (accendingPos.empty()? i:i-accendingPos.top()-1) );
			}
		}

		return area;
    }
};
